import React, {useContext, useEffect, useState}    from 'react'
import useInput from 'client/hooks/useInput'
import useLogin from 'client/hooks/useLogin'
import Link   from 'next/link'
import Router from 'next/router'
import styles from './Login.module.css'
import UserContext from 'pages/index'
import axios from 'axios'
// import WPAPI from 'wpapi'
// // var WPAPI = require(  )
// var wp = new WPAPI({
//     endpoint: window.WP_API_Settings.endpoint,
//     nonce: window.WP_API_Settings.nonce
// })

function index() {
  // console.log(`wp=${wp}`)
  const [email, bindEmail, resetEmail] = useInput('')
  const [pass, bindPass, resetPass] = useInput('')
  const [isLogin, userLogin, userLogout] = useLogin()
  const [loading, setLoading] = useState()
  // console.log(`UserContext = ${UserContext}`)
  // const [user, dispatch] = useContext(UserContext)
  // console.log(`user = ${user}`)


  // useEffect(async () => {
  //   const result = await fetch('localhost:80/wp-json/wp-json/jwt-auth/v1/token', { method: 'POST'})
  //                       .then(res=>{
  //                         console.log(res)
  //                       })
  //                       .catch(err=>{
  //                         console.log(err)
  //                       })
  // }, []);
    // useEffect(async () => {
    //     const result = await fetch('http://localhost/wptest2/?rest_route=/wp/v2/users/me', {
    //       method : 'get',
    //       mode : 'cors',
    //       headers : {
    //         'Access-Control-Allow-Origin' : '*',
    //         'X-WP-Nonce' : _wpnonce
    //       }
    //     })
    //       .then(response => console.log(response.json(), `=====response.json()=====`))
    //       .catch(error => console.log(error, `=====error=====`));
    //   })
  // const getUserNameFromRestApi = () => {
    // console.log(_wpnonce, `=====_wpnonce=====`);

  const submitHandler = e => {
    e.preventDefault()
    const URL = 'localhost:80/wp-json/wp-json/jwt-auth/v1/token'
    axios.post(`http://localhost/wp-json/jwt-auth/v1/token`, {username: email, password: pass})
      .then(res => {
        console.log(`res=`)
        console.log(res)
        console.log(res.data.token)
        localStorage.setItem('token',res.data.token)
        console.log()
      })
      .catch(err => {
        console.log('err=')
        console.log(err)
      })


    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username: email, password:pass })
    };
    fetch('http://localhost/wp-json/jwt-auth/v1/token', requestOptions)
    .then(resf => {
      console.log(`resf=`)
      console.log(resf)
      // console.log(resf.data.token)
    })
    .catch(errf => {
      console.log('errf=')
      console.log(errf)
    })
    // const loginData = {
    //   username: email,
    //   password: pass
    // }
    // setState({loading: true},()=>{
    //   axios.post(URL, loginData)
    //     .then(res=>{
    //       console.log(res)
    //     .catch(err=>{
    //       console.log(err)
    //     })
    //     })
    // })
    // getUserNameFromRestApi()
    if (email==='aaa@hotmail.com' && pass==='test'){
      userLogin(email)
      alert('Success!')
      resetEmail()
      resetPass()
      // Router.push('/')
      // return <Redirect to="/" />;
    } else{
      e.preventDefault()
      alert('Try again!')
      // console.log(user)
      // console.log(user.login)
      // console.log(user.name)
    }

  }
  return (
    <div className={styles.formConatainer}>
        <form onSubmit={submitHandler} className={styles.form}>
          <div className={styles.formOption}>
            <label style={{width:"100px"}}>E-mail:  </label>
            <input {...bindEmail} type="text" className={styles.formTextarea}/>
          </div>
          <div className={styles.formOption}>
            <label style={{width:"100px"}}>Password:</label>
            <input {...bindPass} type="text" className={styles.formTextarea}/>
          </div>
          <div style={{display: 'flex',
                 justifyContent: 'center',
                 alignItems: 'stretch'}}>
            <button className={styles.formSubmit}>Submit</button>
          </div>
        </form>
        <div>
          <Link href="/"><a>Home</a></Link>
          <p>{isLogin ? email : null}</p>
          {/*console.log(`login status is ${isLogin}`)*/}
        </div>
    </div>
  )
}

export default index
