import React from 'react'
import useInput from 'client/components/useInput'
import useLogin from 'client/components/useLogin'
import styles from './Login.module.css'
import Link from 'next/link'
import { useHistory } from "react-router-dom"

function index() {
  const [email, bindEmail, resetEmail] = useInput('')
  const [pass, bindPass, resetPass] = useInput('')
  const [isLogin, userLogin, userLogout] = useLogin()
  let history = useHistory()

  const submitHandler = e => {
    if (email==='aaa@hotmail.com' && pass==='test'){
      userLogin(email)
      e.preventDefault()
      alert('Success!')
      resetEmail()
      resetPass()
      history.push("/")
    } else{
      e.preventDefault()
      alert('Try again!')
    }

  }
  return (
    <div className={styles.formConatainer}>
        <form onSubmit={submitHandler} className={styles.form}>
          <div className={styles.formOption}>
            <label style={{width:"100px"}}>E-mail:  </label>
            <input {...bindEmail} type="text" className={styles.formTextarea}/>
          </div>
          <div className={styles.formOption}>
            <label style={{width:"100px"}}>Password:</label>
            <input {...bindPass} type="text" className={styles.formTextarea}/>
          </div>
          <div style={{display: 'flex',
                 justifyContent: 'center',
                 alignItems: 'stretch'}}>
            <button className={styles.formSubmit}>Submit</button>
          </div>
        </form>
        <div>
          <Link href="/"><a>Home</a></Link>
          <p>{isLogin ? email : null}</p>
          {console.log(`login status is ${isLogin}`)}
        </div>
    </div>
  )
}

export default index
