import React from 'react'
import AddCard from 'client/components/AddCard'
import AddForm from 'client/components/AddForm'

function ListTeams() {
  const [teams, setTeams] = useState([])
  const [add, setAdd] = useState(false)
  const [rerender, setRerender] = useState(false)
  console.log('add now is')
  console.log(add)
  useEffect(() => {
    fetch('http://localhost/wp-json/custom/teams')
    .then(resf => resf.json())
    .then(data => setTeams(data))
    .catch(err => console.log(err))
    return () => {
      // cleanup
    }
  }, [rerender])

  const doRerender = () =>{
    setRerender(!rerender)
    console.log(rerender)
  }
  // console.log('teams=')
  // console.log(teams)
  const teamlist = teams.map(team =>
    <div style={{padding:'10px',
                 margin:'10px',
                 backgroundColor:'#d3d4d5',
                 borderRadius:'5px'}}>
      <img src={team.team_pic} alt=""/>
      <p>Team name: {team.team_name}</p>
      <p>Home court: {team.team_stadium}</p>
      <p>Location: {team.team_state}</p>
    </div>
  )
  console.log(teamlist)
  return (
    <div style={{display: 'flex',
                 justifyContent: 'center',
                 alignItems: 'center'}}>
      <div style={{width:'80vw',
                   display: 'flex',
                   justifyContent: 'center',
                   alignItems: 'center',
                   flexWrap:'wrap'}}>
        {teamlist}
        <div>
          {add ? <div><span onClick={() => setAdd(!add)}>X</span><AddForm doRerender={doRerender}/></div> : <a onClick={() => setAdd(!add)} style={{cursor:'pointer'}}><AddCard /></a>}
        </div>  
      </div>
    </div>
  )
}

export default ListTeams
