import Link from 'next/link'

export default function Show({post}) {
  console.log(post)
  return (
    <div>
      <h1>Title: {post.title.rendered}</h1>
      <p>Post time: {post.date}</p>
      <p>this is a show</p>
      <p>this is the content {JSON.stringify(post)}</p>
     <Link href="/posts/list"><a>Return</a></Link>
    </div>
  )
}

