import Link from 'next/link'

const List = ({ posts }) => {
  const postList = posts.map(post =>
    <div>
      <Link href="/posts/[id]" as={`/posts/${post.id}`}><a key={post.id} postData={`${post}`}><h2>Title: {post.title.rendered}</h2></a></Link>
      <p>Status: {post.status}</p>
      <p>Content: {post.content.rendered}</p>
    </div>)
  return <div>{postList}</div>
}

List.getInitialProps = async ({ req }) => {
  const res = await fetch('http://wp:80/wp-json/wp/v2/posts')
  const json = await res.json()
  return { posts: json }
}

export default List
