import React, {createContext, useReducer}from 'react'
import App from 'next/app';

const userInitial = {
  isLogin: false, 
  name: 'Guest', 
  pass: '', 
  token:'',
  id:null, 
  error:''
}
export const UserContext = createContext(userInitial)

function loginReducer(userInfo, action) {
  switch(action.type) {
      case 'login':
          return {
              ...userInfo,
              isLogin: true,
              name:  action.mail,
              pass:  action.pass,
              id:    action.id,
              token: action.token
          }
      case 'logout':
          return userInitial
      case 'error':
          return {
              ...userInfo,
              name: '',
              pass: '',
              error: action.error
          }
      default: 
          return userInfo;
  }
}

class MyApp extends App {
  render() {
    const [userInfo, dispatch] = useReducer(loginReducer, userInitial)
    const { Component, pageProps } = this.props;

    return (
      <UserContext.Provider value={{loginState: userInfo, loginDispatch: dispatch}}>
        <Component {...pageProps} />
        </UserContext.Provider>
    )
  }
}

export default MyApp;
