import React,{useEffect, useState, useRef} from 'react'
import styles from '../styles/AddForm.module.css'
import useInput from 'client/hooks/useInput'

function AddForm() {
  const inputRef = useRef(null)

  const submitHandler = e => {
    e.preventDefault()

  }

  useEffect(() =>{
    inputRef.current.focus()
  },[])

  return (
    <div className={styles.cardForm}>
      <form onSubmit={submitHandler}>
        <label className={styles.formOptions}>Team's Name:<br/><input className={styles.formTextarea} type="text" ref={inputRef}/></label><br/>
        <label className={styles.formOptions}>Team's City:<br/><input className={styles.formTextarea} type="text"/></label><br/>
        <label className={styles.formOptions}>Team's State:<br/><input className={styles.formTextarea} type="text"/></label><br/>
        <label className={styles.formOptions}>Team's Stadium:<br/><input className={styles.formTextarea} type="text"/></label><br/>
        <label className={styles.formOptions}>Team's Picture:<br/><input className={styles.formTextarea} type="text"/></label><br/>
        <button className={styles.formButton}>Submit</button>
      </form>
    </div>
  )
}

export default AddForm
