const userInitial = {
                      isLogin: false, 
                      name: 'Guest', 
                      pass: '', 
                      token:'', 
                      error:''
                    }
export const UserContext = createContext(userInitial)

function loginReducer(userInfo, action) {
  switch(action.type) {
      case 'login':
          return {
              ...userInfo,
              isLogin: true,
              name:  action.mail,
              pass:  action.pass,
              token: action.token
          }
      case 'logout':
          return userInitial
      case 'error':
          return {
              ...userInfo,
              name: '',
              pass: '',
              error: action.error
          }
      default: 
