import React, {useContext, useEffect}from 'react'
import Link from 'next/link'
import styles from './index.module.css'
import useLogin from 'client/hooks/useLogin'
import Lists from 'client/components/list'

const userInitial = {login: false, name: 'Guest', token:''}
export const UserContext = React.createContext(userInitial)

export default function Index() {
  const [isLogin, userLogin, userLogout, userToken] = useLogin()
  const user = useContext(UserContext)
  console.log(user.name)
  console.log('userToken =')
  console.log(userToken)
  console.log(userToken)
  // console.log(isLogin)
  // console.log(wpApiSettings.root)
  if (false){
    const requestOptions1 = {
      method: 'GET',
      headers: { 'X-WP-Nonce': userToken,
                //  'Access-Control-Allow-Origin': '*',
                //  'Access-Control-Allow-Methods': 'GET,PUT,POST,DELETE,PATCH,OPTIONS' 
                },
      // body: JSON.stringify({ title: "change!" }),
      credentials: 'include' // custom plugin then add that page
    };
    fetch('http://localhost/wp-json/wp/v2/users/me',requestOptions1)
      .then(resf1 => resf1.json()) // necessary!
      .then(data1 => {
        console.log('in fetch, me is ')
        console.log(data1)
      })
      // .catch(err  => console.log(err))
  }

  useEffect(() => {
    localStorage.setItem('userToken', userToken)
    return () => {
      // cleanup
    }
  }, [])

  return (
    <>
      <UserContext.Provider>
        <div className={styles.nav}>
          <Link href="/"><a>Home</a></Link>
          <h1>MAC posts</h1>
            <div>
              <span>{user.name}   </span>
              { user.login ?
              <Link href="/"><a onClick={userLogout()}>Log out</a></Link>
              : <Link href="/login"><a>Log In</a></Link>
              }
            </div>

        </div>
        <div style={{display: 'flex',
                     justifyContent: 'center',
                     alignItems: 'center'}}>
          <div style={{width:'80vw'}}>
            <Lists />
          </div>
        </div>
      </UserContext.Provider>
      <style jsx global>{`body { margin:0px; }`}</style>
      {/* <script>
        <?php echo "hello world"; ?>
      </script> */}
    </>
  )
}
