import React from 'react'
import useInput from 'client/components/useInput'

function index() {
  const [email, bindEmail, resetEmail] = useInput('')
  const [pass, bindPass, resetPadd] = useInput('')

  const submitHandler = e => {
    if (email==='aaa@hotmail.com' && pass==='test'){
      e.preventDefault()
      alert('Success!')
      resetEmail()
      resetPass()
    } else{
      e.preventDefault()
      alert('Try again!')
    }

  }
  return (
    <div>
      <form onSubmit={submitHandler}>
        <div>
          <label>E-mail:</label>
          <input {...bindEmail} type="text"/>
        </div>
        <div>
          <label>Password:</label>
          <input {...bindPass} type="text"/>
        </div>
        <button>Submit</button>
      </form>
    </div>
  )
}

export default index
