import React, {useState, useEffect} from 'react'
import styles from './ListAll.module.css'


function ListAll() {
  const [teams, setTeams] = useState([])
  const [players, setPlayers] = useState([])
  const [add, setAdd] = useState(false)
  const [rerender, setRerender] = useState(false)
  useEffect(() => {
    fetch('http://localhost/wp-json/custom/teams')
    .then(resf => resf.json())
    .then(data => setTeams(data))
    .catch(err => console.log(err))
    return () => {
      // cleanup
    }
  }, [rerender])

  useEffect(() => {
    fetch('http://localhost/wp-json/custom/players')
    .then(resf => resf.json())
    .then(data => setPlayers(data))
    .catch(err => console.log(err))
    return () => {
      // cleanup
    }
  }, [])
  const team1 = players.filter(player => player.team_id == 1)
  console.log(team1)
  const doRerender = () =>{
    setRerender(!rerender)
    console.log(rerender)
  }
  // console.log('teams=')
  // console.log(teams)
  const teamlist = teams.map((team,index) =>
    <div className={styles.card} key={index}>
      <p>Team name: {team.team_name}</p>
      <p>Home court: {team.team_stadium}</p>
      <p>Location: {team.team_state}</p>
    </div>
  )
  return (
    <div className='contentContainer'>
      <div style={{width:'80vw',
                   display: 'flex',
                   justifyContent: 'center',
                   alignItems: 'center',
                   flexWrap:'wrap',
                   flexDirection:'column'}}>
        <div style={{width:'100%',height:'100%'}}>
        {teamlist}
        </div>
      </div>
    </div>
  )
}

export default ListAll
