import React,{useContext} from 'react'
import useInput from 'client/hooks/useInput'
import {UserContext} from 'pages/index'
import Router from 'next/router'

function LoginForm() {
  const [email, bindEmail, resetEmail] = useInput('')
  const [pass, bindPass, resetPass] = useInput('')
  const userContext = useContext(UserContext)
  const toeknUrl = process.env.getTokenUrl

  const submitHandler = e => {
    // ----------fetch version get token---------------
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username: email, password:pass })
    };
    fetch(toeknUrl, requestOptions)
    .then(resf => resf.json())
    .then(data => {
      if(data.token){
        // success login
        userContext.loginDispatch({type:'login', token:data.token, mail:email, pass:pass})
        Router.push('/')
      } else {
        // wrong information
        userContext.loginDispatch({type:'error', error:'wrong email or password'})
        resetEmail('')
        resetPass('')
      }
    })
    .catch(err => {
      // other errors
      userContext.loginDispatch({type:'error', error:error})
    })
    // ------------------------------------------------
    e.preventDefault()
  }

  return (
    <div>
      <form onSubmit={submitHandler}>
        {userContext.loginState.error && !email && <h2>wrong email or passwor</h2>}
        <div>
          <label>E-mail: </label>
          <input {...bindEmail} type="text"/>
          <label >Password: </label>
          <input {...bindPass} type="text"/>
        </div>
        <button>Submit</button>
      </form>
    </div>
  )
}

export default LoginForm
