import React, {createContext, useContext, useEffect, useReducer}from 'react'
import Link from 'next/link'
import styles from './index.module.css'
import useLogin from 'client/hooks/useLogin'
import Lists from 'client/components/list'
import LoginForm from 'client/components/LoginForm'

const userInitial = {
                      isLogin: false, 
                      name: 'Guest', 
                      pw: '', 
                      token:'', 
                      error:''
                    }
export const UserContext = createContext(userInitial)

function loginReducer(userInfo, action) {
  switch(action.type) {
      case 'login':
          return {
              ...userInfo,
              isLogin: true,
              name:  action.mail,
              pw:    action.pw,
              token: action.token
          }
      case 'success':
          return {
              ...userInfo,
              isLogin: true,
          }
      case 'error':
          return {
              ...userInfo,
              name: '',
              pw: '',
              error: action.error
          }
      default: 
          return userInfo;
  }
}

export default function Index() {
  const [isLogin, userLogin, userLogout, userToken] = useLogin()
  const user = useContext(UserContext)
  console.log(`user.isLogin=${user.isLogin}`)
  // console.log(user.name)
  // console.log('userToken =')
  // console.log(userToken)
  // console.log(userToken)
  const [userInfo, dispatch] = useReducer(loginReducer, userInitial)
  console.log(userInfo)
  // console.log(isLogin)
  // console.log(wpApiSettings.root)
  // if (false){
  //   const requestOptions1 = {
  //     method: 'GET',
  //     headers: { 'X-WP-Nonce': userToken,
  //               //  'Access-Control-Allow-Origin': '*',
  //               //  'Access-Control-Allow-Methods': 'GET,PUT,POST,DELETE,PATCH,OPTIONS' 
  //               },
  //     // body: JSON.stringify({ title: "change!" }),
  //     credentials: 'include' // custom plugin then add that page
  //   };
  //   fetch('http://localhost/wp-json/wp/v2/users/me',requestOptions1)
  //     .then(resf1 => resf1.json()) // necessary!
  //     .then(data1 => {
  //       console.log('in fetch, me is ')
  //       console.log(data1)
  //     })
  //     // .catch(err  => console.log(err))
  // }

  // useEffect(() => {
    // const user = useContext(UserContext)
    // return () => {
    //   // cleanup
    // }
  // }, [])

  return (
    <>
      <UserContext.Provider value={{loginState: userInfo, loginDispatch: dispatch}}>
        <div className={styles.nav}>
          <Link href="/"><a>Home</a></Link>
          <h1>MAC posts</h1>
            <div>
              <span>{userInfo.name}   </span>
              { userInfo.isLogin ?
              <Link href="/"><a onClick={userLogout()}>Log out</a></Link>
              : <Link href="/login"><a>Log In</a></Link>
              }
            </div>
        </div>
        { userInfo.isLogin 
        ? <Lists />
        : <LoginForm />}

      </UserContext.Provider>
      <style jsx global>{`body { margin:0px; }`}</style>
      {/* <script>
        <?php echo "hello world"; ?>
      </script> */}
    </>
  )
}
