import React,{useContext} from 'react'
import useInput from 'client/hooks/useInput'
import {UserContext} from 'pages/index'

function LoginForm() {
  const [email, bindEmail, resetEmail] = useInput('')
  const [pass, bindPass, resetPass] = useInput('')
  const userContext = useContext(UserContext)

  const submitHandler = e => {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username: email, password:pass })
    };
    fetch(toeknUrl, requestOptions)
    .then(resf => resf.json()) // necessary!
    .then(data => {
      if(data.token){
        console.log(data)
        // userLogin(email, data.token)
        userContext.loginDispatch({type:'login', payload:{token:data.token, email:email, pw:pass}})
        // Router.push('/')
      } else {
        console.log("login fail")
      }
    })
    .catch(errf => {
      console.log('errf=')
      console.log(errf)
    })
    userContext.loginDispatch({type:'login'})
    e.preventDefault()
    // alert(`Hello ${firstName} ${lastName}`)
    // console.log(email)
    // console.log(pass)
    // console.log('click')
  }

  return (
    <div>
      <form onSubmit={submitHandler}>
        <div>
          <label>E-mail: </label>
          <input {...bindEmail} type="text"/>
          <label >Password: </label>
          <input {...bindPass} type="text"/>
        </div>
        <button>Submit</button>
      </form>
    </div>
  )
}

export default LoginForm
