import {useState} from 'react'

function useLogin(userInfo = '') {
  const [login, setLogin] = useState(false)
  const [name, setName] = useState('')

  const userLogin = ()  => {
    setLogin(true)
    setName(userInfo)
  }

  const userLogout = ()  => {
    setLogin(false)
  }

  return [login, userLogin, userLogout]
}

export default useLogin
