    //   } else {
    //     console.log("login fail")
    //   }
    // })
    // .catch(errf => {
    //   console.log('errf=')
    //   console.log(errf)
    // })
    userContext.loginDispatch({type:'login'})
    e.preventDefault()
    // alert(`Hello ${firstName} ${lastName}`)
    resetFirstName()
    resetLastName()
    console.log('click')
  }

  return (
    <div>
      <form onSubmit={submitHandler}>
        <div>
          <label>E-mail: </label>
          <input {...bindEmail} type="text"/>
          <label >Password: </label>
          <input {...bindPass} type="text"/>
        </div>
        <button>Submit</button>
      </form>
    </div>
  )
}

export default LoginForm
