import React, {useContext}from 'react'
import Link from 'next/link'
import styles from './index.module.css'
import useLogin from 'client/components/useLogin'

const userInitial = {login: false, name: 'haha'}
export const UserContext = React.createContext(userInitial)

export default function Index() {
  const [isLogin, userLogin, userLogout] = useLogin()
  const user = useContext(UserContext)

  return (
    <>
      <UserContext.Provider>
        <div className={styles.nav}>
          <Link href="/"><a>Home</a></Link>
          <h1>MAC posts</h1>
          { user.login ?
            <div>
              <span>{user.name}</span> <a>Log out</a>
            </div>
            : <Link href="/login"><a>Log In</a></Link>
          }

        </div>
        <p>Hello Next.js, this is your friend Brian from logrooooooddddddocket</p>
        <Link href="/posts/page"><a>test</a></Link><br/>
      </UserContext.Provider>
    </>
  )
}
